(1, '<main>\r\n  <div class="container">\r\n\r\n  <article class="col-md-offset-3 col-md-9 col-xs-12">\r\n    <h1>Оглавление</h1>\r\n  <div class="panel-body">\r\n          <ul>\r\n            <li><a href="/11.html" >1.1. Стратегия инвестирования</a></li>\r\n            <li><a href="/12.html" >1.2. Экономическая результативность</a></li>\r\n            <li><a href="#" >1.3. Корпоративное управление</a></li>\r\n            <li><a href="#" >1.4. Закупочная деятельность</a></li>\r\n            <li><a href="#" >1.5. Риск-менеджмент</a></li>\r\n            <li><a href="#" >1.6. Этика бизнеса и противодействие коррупции</a></li>\r\n            <li><a href="#" >1.7. Управление персоналом</a></li>\r\n            <li><a href="#" >1.8. Взаимодействие с заинтересованными сторонами</a></li>\r\n            <li><a href="#" >1.9. Управление корпоративной социальной ответственностью</a></li>\r\n            <li><a href="#" >1.10. Риск-менеджмент</a></li>           \r\n          </ul>\r\n\r\n          <ul>\r\n            <li><a href="#">2.1. Инвестиции в человеческий капитал</a></li>\r\n            <li><a href="#">2.2. Развитие регионов и сообществ</a></li>\r\n            <li><a href="#">2.3. Технологическое лидерство</a></li>\r\n            <li><a href="#">2.4. Зеленая экономика</a></li>\r\n          </ul>\r\n\r\n          <ul>\r\n            <li><a href="/interactive_analytics.html" >Интерактивная аналитика</a></li>\r\n            <li><a href="#" >Ключевые показатели</a></li>\r\n            <li><a href="#" >Календарь событий</a></li>\r\n            <li><a href="/honors.html" >Награды и достижения</a></li>\r\n            <li><a href="/chairman.html" >Обращение Председателя Совета директоров</a></li>\r\n            <li><a href="#" >Повестка в области устойчивого развития</a></li>\r\n            <li><a href="#" >Контакты</a></li>\r\n            <li><a href="#" >Глоссарий</a></li>\r\n            <li><a href="#" >Список сокращений</a></li>\r\n            <li><a href="/apps.html" >Приложения</a></li>\r\n          </ul>\r\n        </div>\r\n\r\n\r\n    \r\n\r\n</article>\r\n\r\n\r\n\r\n  \r\n</div><!--/container между main и article-->\r\n\r\n</main>\r\n<!-- </div> /main-container -->', 'English', 'Оглавление', 'Contents', 1),
